void PrintWeaponName(int weapon)
{
  switch (weapon)
  {
    case W_O_STILETTO:    PrintCompText("\xc5\x69\xcf\x74\x74\x6f");    break;
    case W_O_AXE:         PrintCompText("\x62\xd9\x6f\x64\xc4\x61\x78\x65");  break;
    case W_O_SWORD:       PrintCompText("\x73\x77\x6f\x72\x64");       break;
    case W_O_KNIFE:       PrintCompText("\x6e\xe0\x74\xc4\x6b\x6e\x69\x66\x65"); break;
    case W_O_RUSTY_KNIFE: PrintCompText("\x72\xfe\x74\xc4\x6b\x6e\x69\x66\x65"); break;
  }
}

